import React, { Component } from 'react';

class TextInput extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return(
      <div>
        <label>
          {`${this.props.label}: `}
          <input type={this.props.type} maxLength={this.props.length} required></input>
        </label>
      </div>
    );
  }

}

export default TextInput;
