import React from 'react';
import './App.css';
import TextInput from './TextInput';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nameInput: '',
    }
    this.handleInput = this.handleInput.bind(this);
  }

  handleInput = (event) => {
    console.log(event);
    this.setState((state) => ({
      nameInput: event.target.value
    }))
  }

  render() {
    return (
      <div className="App">
        <h1>My first React form</h1>
        <form>
          <fieldset>
            <TextInput label='Name' type='text' length='40' value={this.state.nameInput} onChange={this.handleInput} />
            <TextInput label='E-mail' type='text' length='50' />
          </fieldset>
          <fieldset>

          </fieldset>
        </form>
      </div>
    );
  }

}

export default App;
