import React, { Component } from 'react';

class TextInput extends Component {

  constructor(props) {
    super(props);
    this.state = {
      nameInput: '',
    }
  }

  handleInput = (event) => {
    console.log(event);
    this.setState({
      nameInput: event.target.value
    });
  }

  render() {
    return(
      <div>
        <label>
          {`${this.props.label}: `}
          <input type={this.props.type} maxLength={this.props.length} required value={this.state.nameInput} onChange={this.handleInput}></input>
        </label>
      </div>
    );
  }

}

export default TextInput;
