  //   this.setState({
  //     nameInput: event.target.value.toUpperCase(),
  //     cpfInput: '',
  //   });
  // }

  render() {
    return(
      <div>
        <label>
          {`${this.props.label}: `}
          <input type={this.props.type} maxLength={this.props.length} required></input>
        </label>
      </div>
    );
  }
}

export default TextInput;
