import React, { Component } from 'react';

class TextInput extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: '',
    };
  }

  handleInput = (event) => {
    this.setState({
      input: event.target.value.toUpperCase(),
    })
  }

  render() {
    return(
      <label>
        {`${this.props.label}: `}
        <input type={this.props.type} maxLength={this.props.length} value={this.state.input} onChange={this.handleInput}></input>
      </label>
    );
  }

}

export default TextInput;
