import React, { Component } from 'react';

class Forms extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nameInput: '',
      cpfInput: '',
    }
  }

  handleInput = (event) => {
    console.log(event);
    this.setState({
      nameInput: event.target.value.toUpperCase(),
      cpfInput: event.target.value,
    });
  }

  render() {
    return(
      <div>
        <label>
          Name: 
          <input type='text' maxLength='40' required onChange={this.handleInput} value={this.state.nameInput}></input>
        </label>
        <label>
          CPF: 
          <input type='number' maxLength='11' required onChange={this.handleInput} value={this.state.cpfInput}></input>
        </label>
      </div>
    );
  }
}

export default Forms;
